xwikidoc.content:
{{groovy}}
  import groovy.json.JsonSlurper;

  def xwikiPropertiesConfiguration = services.component.getContextComponentManager().getInstance(org.xwiki.configuration.ConfigurationSource.class, 'xwikiproperties');

  def apiKey = xwikiPropertiesConfiguration.getProperty('deepl.api.key');
  def apiDomain = xwikiPropertiesConfiguration.getProperty('deepl.api.domain');

  def deeplGetRequest(apiDomain, apiKey, path, withJson = true) {
    def deeplConnection = "${apiDomain}${path}".toURL().openConnection();
    deeplConnection.setRequestProperty('Authorization', "DeepL-Auth-Key ${apiKey}")
    if (deeplConnection.getResponseCode() == 200) {
      def deeplResponse = deeplConnection.getInputStream().getText();
      if (withJson) {
        return new JsonSlurper().parseText(deeplResponse);
      } else {
        return deeplResponse;
      }
    }
  }

  def usage = deeplGetRequest(apiDomain, apiKey, '/v2/usage');
  println "* Character count : ${usage['character_count']}";
  println "* Character limit : ${usage['character_limit']}";
{{/groovy}}
